sql.where:
1 = 1
   		 and 	mdl_validity 		like "%"#{mdlValidity}"%"
   		 		 and 	mdl_code 			like "%"#{mdlCode}"%"
   		         and 	mdl_ntype 			like "%"#{mdlNtype}"%"
   			     and 	mdl_stay 			like "%"#{mdlStay}"%"
