   		             and 	mdl_num 			  = 	 #{mdlNum}
   	         and 	mdl_address 	      = 	 #{mdlAddress}

mapper.update:
UPDATE
	management_detail
SET
	mdl_code  =  #{mdlCode}
WHERE
	id = #{id}

mapper.delete:
UPDATE
	management_detail
SET
	mdl_is_del_status  = #{mdlIsDelStatus}
WHERE
	id = #{id}
